//React, redux
import React from 'react'
import { connect } from 'react-redux';

//UI
import { toast } from 'react-toastify';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.min.css';
import constants from '../../../../resources/strings'

//provider
import dataCacheProvider from '../../../../data-access/datacache-provider'
import userProvider from '../../../../data-access/user-provider'

import './main.css'
import './util.css' 
import axios from 'axios';


class Login extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            username: '',
            password: '',
            grant_type: 'password'
            // confirm: ''
        }
    }

    componentWillMount() {
        this.checkUserLogin()
    }

    checkUserLogin() {
        if (this.props.userApp.currentUser) {
            this.props.history.push("/admin/dashboard");
        } else {
            this.props.history.push("/dang-nhap");
        }
    }

    login() {
        const { username, password, grant_type } = this.state;
        let body = {
          email: username,
          password: password
        }
        userProvider.login(body).then(res=>{
          this.props.dispatch({ type: constants.action.action_user_login, value: res.user })
          this.props.dispatch({ type: 'SAVE_TOKEN', value: res.token })
          dataCacheProvider.save("", constants.key.storage.current_account, res.user).then(s => {
              this.props.history.push("/admin/dashboard");
          });
          toast.success('Xin chÃ o '+ res.user.username, {
              position: toast.POSITION.TOP_RIGHT
          });
        }).catch(e=>{
          console.log(e)
        })
    }


    render() {
        const { username, password, confirm } = this.state;
        return (
            <div className="limiter">
            <div className="container-login100">
              <div className="wrap-login100">
                <div className="login100-form-title" style={{backgroundImage: 'url(images/bg-01.jpg)'}}>
                  <span className="login100-form-title-1">
                    <sub style={{fontWeight:100, fontSize:10}}>Admin</sub>
                  </span>
                </div>
                <form className="login100-form validate-form">
                  <div className="wrap-input100 validate-input m-b-26" data-validate="Username is required">
                    <span className="label-input100">Username</span>
                    <input value={username} onChange={(event) => { this.setState({ username: event.target.value }) }}  className="input100" type="text" name="username" placeholder="Enter username" />
                    <span className="focus-input100" />
                  </div>
                  <div className="wrap-input100 validate-input m-b-18" data-validate="Password is required">
                    <span className="label-input100">Password</span>
                    <input  value={password} onChange={(event) => { this.setState({ password: event.target.value }) }}  className="input100" type="password" name="pass" placeholder="Enter password" />
                    <span className="focus-input100" />
                  </div>
                  <div className="flex-sb-m w-full p-b-30">
                    <div className="contact100-form-checkbox">
                      <input className="input-checkbox100" id="ckb1" type="checkbox" name="remember-me" />
                      <label className="label-checkbox100" htmlFor="ckb1">
                        Remember me
                      </label>
                    </div>
                    <div>
                      <a href="#" className="txt1">
                        Forgot Password?
                      </a>
                    </div>
                  </div>
                  <div className="container-login100-form-btn">

                  </div>
                </form>
                <button onClick={() => this.login()}  className="login100-form-btn">
                      Login
                    </button>
              </div>
            </div>
          </div>
        )
    }
}

function mapStateToProps(state) {
    return {
        userApp: state.userApp
    };
}
export default connect(mapStateToProps)(Login);
